mapper.select:
SELECT dataset_table_task_log.*, dataset_table_task.name, dataset_table.name as dataset_name FROM dataset_table_task_log LEFT JOIN dataset_table_task ON dataset_table_task_log.task_id = dataset_table_task.id LEFT JOIN dataset_table ON dataset_table_task_log.table_id = dataset_table.id
order by ${orderByClause}
ORDER BY dataset_table_task_log.create_time desc

SELECT dataset_table_task_log.*, dataset_table_task.name, dataset_table.name as dataset_name FROM dataset_table_task_log LEFT JOIN dataset_table_task ON dataset_table_task_log.task_id = dataset_table_task.id LEFT JOIN dataset_table ON dataset_table_task_log.table_id = dataset_table.id
order by ${orderByClause}
ORDER BY dataset_table_task_log.create_time desc

SELECT dataset_table.name as table_name, 'grant,manage,use' as `privileges`,dataset_table_task.* , qrtz_triggers.NEXT_FIRE_TIME FROM dataset_table_task left join dataset_table on dataset_table.id=dataset_table_task.table_id left join qrtz_triggers on dataset_table_task.id=qrtz_triggers.TRIGGER_NAME
order by ${orderByClause}
order by dataset_table_task.create_time desc

